import React from 'react';
import './FeaturesSection.css';

const features = [
  'mello',
  'Emotionally Intelligent AI Companion',
  'Gentle, Human-Like Experience',
  'Memory-Powered Conversations',
  'Voice Chat Support',
];

const FeaturesSection = () => (
  <section className="features-section">
    <div className="features-img flower-glow-img" />
    <div className="features-content">
      <h2>What makes <span className="mello-name">Mello</span> <span className="unique">unique</span> ?</h2>
      <ol className="features-list">
        {features.map((feature, idx) => (
          <li key={idx}><span className="feature-num">{String(idx+1).padStart(2, '0')}</span> {feature}</li>
        ))}
      </ol>
    </div>
  </section>
);

export default FeaturesSection;
