import React, { useState } from 'react';
import './ComingSoon.css';

const ComingSoon = () => {
    const [email, setEmail] = useState('');
    const [status, setStatus] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setStatus('');
        try {
            const response = await fetch('http://localhost:5000/api/notify', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email })
            });
            if (response.ok) {
                setStatus('Thank you! You will be notified.');
                setEmail('');
            } else {
                const data = await response.json();
                setStatus(data.error || 'Something went wrong.');
            }
        } catch (err) {
            setStatus('Network error. Please try again later.');
        }
    };

    return (
        <div className="coming-soon-container">
            <div className="content">
                <h1 className="gradient-text">Coming Soon</h1>

                <form onSubmit={handleSubmit} className="email-form">
                    <input
                        type="email"
                        placeholder="Please enter your email address"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <button type="submit">Notify Me</button>
                </form>
                {status && <p className="notify-text">{status}</p>}
                <p className="notify-text">Notify me when app launched</p>
            </div>

            <footer>
                <div className="footer-left">
                </div>
                <div className="footer-right">
                    <a href="https://instagram.com">Instagram</a>
                    <span className="separator"> / </span>
                    <a href="https://linkedin.com">X.com</a>
                </div>
            </footer>
        </div>
    );
};

export default ComingSoon;
